package org.simol.examples.design_patterns.dao;import java.util.List;public class Main{public static void main(String[]args){UserDao userDao=new UserDaoMemory();User newUser=new User(1,"john_doe","john@example.com");userDao.save(newUser);List<User>users=userDao.findAll();for(User user:users){System.out.println("User: "+user.getUsername()+", Email: "+user.getEmail());}User updatedUser=new User(1,"new_username","new_email@example.com");userDao.update(updatedUser);User retrievedUser=userDao.findById(1);if(retrievedUser!=null){System.out.println("Updated User: "+retrievedUser.getUsername()+", Email: "+retrievedUser.getEmail());}userDao.delete(1);User deletedUser=userDao.findById(1);if(deletedUser==null){System.out.println("User with ID 1 has been deleted.");}}}