package org.simol.examples.design_patterns.composite.improvements;import java.util.ArrayList;import java.util.List;class Folder implements FileSystemComponent{private String name;private List<FileSystemComponent>children=new ArrayList<>();public Folder(String name){this.name=name;}public void add(FileSystemComponent component){children.add(component);}public void remove(FileSystemComponent component){children.remove(component);}public void done(){this.display();for(FileSystemComponent component:children){component.done();}}private void display(){System.out.println("Folder: "+name);}}